	//@Test
	public void sequenceCompressPeformanceTest() throws IOException, InterruptedException{

		String filePath		=	"C:\\word.txt";
		String encode		=	"UTF-8";
		BufferedReader br	=	new BufferedReader(
									new InputStreamReader(
											new FileInputStream(filePath), encode) );

		int pForNum	 = 0;
		int dataNum	 = 0;

		while( br.ready() ){

			String line = br.readLine();
			dataNum++;
			if( dataNum % 10000 == 0 )
				System.out.println( "now line : " + dataNum );
			String[] part = line.split(":");
			if( part.length != 2 )
				throw new IllegalArgumentException();

			String[] strIds = part[1].split("\t");
			if( strIds == null || strIds.length <= 0 )
				throw new IllegalArgumentException();
			int[] ids		= new int[strIds.length];
			for( int i = 0 ; i < strIds.length ; i++  )
				ids[i] = Integer.parseInt( strIds[i] );

			boolean useGapList = true;
			try{
				boolean useArraySorte = true;
				int[] encoded = code.encode(ids, useArraySorte, useGapList);
				pForNum  += encoded.length;
				int[] decoded = code.decode( encoded , useGapList );
				assertEquals(ids.length , decoded.length);
			}
			catch( RuntimeException e ){
				System.out.println(line);
				throw e;
			}

		}
		br.close();

		System.out.println( "pForNum : "  + pForNum * 4  + 		 "byte" );

	}


	@Test
	public void deCompressPeformanceTest2() throws Exception{

		String fileName	 =	"word.txt";
		String encode	 =	"UTF-8";

		int dataNum	 = 0;
		int intCount = 0;
		int dataCount = 0;
		List<int[]> encodedDatam = new ArrayList<int[]>();

			boolean useGapList = true;
			InputStream is = Thread.currentThread().getContextClassLoader().getResourceAsStream(fileName);
			BufferedReader br	=	new BufferedReader(
										new InputStreamReader( is, encode ) );

			while( br.ready() ){

				String line = br.readLine();
				dataNum++;
				if( dataNum % 10000 == 0 )
					System.out.println( "now line : " + dataNum );

				String[] part = line.split(":");
				if( part.length != 2 )
					throw new IllegalArgumentException();

				String[] strIds = part[1].split("\t");
				if( strIds == null || strIds.length <= 0 )
					throw new IllegalArgumentException();

				int dataLength = strIds.length;
				dataCount += dataLength;
				int[] ids = new int[ dataLength ];

				for( int i = 0 ; i < strIds.length ; i++  )
					ids[i] = Integer.parseInt( strIds[i] );

				boolean useArraySorte = true;
				int[] encoded = code.encode( ids, useArraySorte, useGapList );
				intCount += encoded.length;
				encodedDatam.add(encoded);

			}
			br.close();

		int i = 0;
		System.out.println("dataSize : "+encodedDatam.size());

		try{

			long start = System.currentTimeMillis();

			for(int num = 0 ; num < 10 ; num ++ ){
				for( ; i < encodedDatam.size() ; i++  )
					code.decode( encodedDatam.get(i), false );
				i = 0;
			}
			long end = System.currentTimeMillis();
			long diff = end - start;
			System.out.println(diff);
			System.out.println("dataNum : " + encodedDatam.size());
			System.out.println("original : " + dataCount * 4 + "byte");
			System.out.println("compress : " + intCount * 4 + "byte");
		}
		catch (Exception e) {
			System.out.println("error line : "+i);
			throw e;
		}

	}



